// 1️⃣ Access Cal.com webhook structure correctly
const data = $node["Status"].json.body.payload.responses;

// 2️⃣ Extract company name and email
const companyName = data.companyName?.value || null;
const email = data.email?.value?.toLowerCase() || null;

// 3️⃣ Extract domain from email
let domain = null;
if (email) {
  const match = email.match(/@(.+)/);
  domain = match ? match[1] : null;
}

// 4️⃣ Skip common personal email providers
const personalDomains = [
  "gmail.com",
  "yahoo.com",
  "hotmail.com",
  "outlook.com",
  "live.com",
  "icloud.com",
  "protonmail.com",
  "me.com",
  "aol.com"
];

let website = null;
if (domain && !personalDomains.includes(domain)) {
  website = `https://${domain}`;
}

// 5️⃣ Build the Allabolag search URL (encode spaces safely)
const allabolagUrl = companyName
  ? `https://www.allabolag.se/bransch-s%C3%B6k?q=${encodeURIComponent(companyName)}`
  : null;

// 6️⃣ Return trimmed data
return [
  {
    domain,
    website,
    allabolagUrl,
    skipped: !website, // For logging/debugging
    reason: !website ? "Personal or invalid domain" : "Valid company domain"
  }
];

// 1️⃣ Get HTML data from the previous node
let html = $json.data || "";

// 2️⃣ Extract page title
const titleMatch = html.match(/<title>(.*?)<\/title>/i);

// 3️⃣ Remove <script> and <style> blocks
html = html
  .replace(/<script[\s\S]*?<\/script>/gi, " ")
  .replace(/<style[\s\S]*?<\/style>/gi, " ");

// 4️⃣ Strip all remaining HTML tags
let text = html.replace(/<[^>]+>/g, " ");

// 5️⃣ Decode common HTML entities
text = text
  .replace(/&amp;/g, "&")
  .replace(/&quot;/g, '"')
  .replace(/&#39;/g, "'")
  .replace(/&lt;/g, "<")
  .replace(/&gt;/g, ">");

// 6️⃣ Remove cookie / footer / navigation / legal noise
text = text.replace(
  /(cookie|integritetspolicy|användarvillkor|consent|preferences|reject|accept|privacy policy|thank you! your submission|oops! something went wrong|manage by category|analytics measures usage)/gi,
  " "
);
text = text.replace(/\b(Hem|Om oss|Tjänster|Blogg|Kontakta oss|FAQ|Sidor|English|Svenska|Företaget|Sitemap)\b/gi, " ");

// 7️⃣ Collapse whitespace
text = text.replace(/\s+/g, " ").trim();

// 8️⃣ Optionally cut off anything after © (usually footer)
const cutoff = text.indexOf("©");
if (cutoff > 0) text = text.slice(0, cutoff).trim();

// 9️⃣ Return clean text + title only
return [
  {
    title: titleMatch ? titleMatch[1] : null,
    cleanText: text
  }
];

// Extract start time from the "Status" node
const startTime = $('Status').item.json.body.payload.startTime;

// Optionally extract or define end time
const endTime = $('Status').item.json.body.payload.endTime;

// Parse dates
const startUTC = new Date(startTime);
const endUTC = endTime ? new Date(endTime) : new Date(startUTC.getTime() + 30 * 60 * 1000); // default 30 min if missing

// Adjust to local timezone automatically
// (Handles CET/CEST daylight saving)
const localOffset = startUTC.getTimezoneOffset() * 60 * 1000;
const startLocal = new Date(startUTC.getTime() - localOffset);
const endLocal = new Date(endUTC.getTime() - localOffset);

// Calculate duration in milliseconds
const durationMs = endLocal - startLocal;

// Convert to hours, minutes, seconds
const hours = String(Math.floor(durationMs / 1000 / 60 / 60)).padStart(2, '0');
const minutes = String(Math.floor((durationMs / 1000 / 60) % 60)).padStart(2, '0');
const seconds = '00';

// Format output
const due_date = startLocal.toISOString().slice(0, 10);   // YYYY-MM-DD
const due_time = startLocal.toISOString().slice(11, 19);  // HH:mm:ss
const duration = `${hours}:${minutes}:${seconds}`;        // HH:mm:ss

// Return in a clean JSON object
return [{ due_date, due_time, duration }];

